// for main /maps page - creating table with sides names:
function createTR(name, link, city, type, side, code) {
    const tr = document.createElement('tr');
    tr.setAttribute("id", code);

    const td1 = document.createElement('td');
    const a = document.createElement('a');
    const text1 = document.createTextNode(name);
    a.setAttribute('href', link);
    a.setAttribute('target', '_blank');
    a.appendChild(text1);
    td1.appendChild(a);
    tr.appendChild(td1);

    const td2 = document.createElement('td');
    const text2 = document.createTextNode(city);
    td2.appendChild(text2);
    tr.appendChild(td2);

    const td3 = document.createElement('td');
    const text3 = document.createTextNode(type);
    td3.appendChild(text3);
    tr.appendChild(td3);

    const td4 = document.createElement('td');
    const text4 = document.createTextNode(side);
    td4.appendChild(text4);
    tr.appendChild(td4);

    return(tr);
}

const tableList = document.getElementById('tbody');
if (tableList) data.map(function(e) {
  tableList.appendChild(createTR(e.name, e.code + '/', e.city, e.type, e.side, e.code));
});


// for inner detailed one-side /maps/page - filling side data on page
const sideName = document.getElementById('sideName');
const sideType = document.getElementById('sideType');
const sideCity = document.getElementById('sideCity');
const sideSide = document.getElementById('sideSide');

const sideFotoImgLink = document.getElementById('sideFotoImgLink');
const sideFotoImg = document.getElementById('sideFotoImg');
const sidePresImgLink = document.getElementById('sidePresImgLink');
const sidePresImg = document.getElementById('sidePresImg');
const sideDivMap = document.getElementById('sideDivMap');

if (sideName) {
  // finding index of current page element in data array
  const sideCode = window.location.pathname.slice(6, -1);
  let dataIndex;
  for (dataIndex = 0; dataIndex < data.length; dataIndex++) // FUCK IE
    if (data[dataIndex].code == sideCode) break;
  const textSideName =  document.createTextNode(data[dataIndex].name);
  sideName.appendChild(textSideName);

  const textSideType =  document.createTextNode(data[dataIndex].type);
  sideType.appendChild(textSideType);

  const textSideCity =  document.createTextNode(data[dataIndex].city);
  sideCity.appendChild(textSideCity);

  const textSideSide =  document.createTextNode(data[dataIndex].side);
  sideSide.appendChild(textSideSide);

  sideFotoImgLink.href = '/foto/' + data[dataIndex].code + '.jpg';
  sideFotoImg.src = '/foto/' + data[dataIndex].code + '.jpg';

  const presCode = data[dataIndex].code.split('_').slice(0, -1).join('_');
  sidePresImgLink.href = '/pres/' + presCode + (data[dataIndex].type == 'Видеоэкран' ? 'V' : '') + '.jpg';
  sidePresImg.src = '/pres/' + presCode + (data[dataIndex].type == 'Видеоэкран' ? 'V' : '') + '.jpg';

  const sideMapIframe = document.createElement('iframe');
  sideMapIframe.setAttribute('src', 'https://yandex.ru/map-widget/v1/?ll=' + data[dataIndex].y + ',' + data[dataIndex].x + '&pt=' + data[dataIndex].y + ',' + data[dataIndex].x + '&z=16');
  sideMapIframe.setAttribute('width', '233');
  sideMapIframe.setAttribute('height', '175');
  sideDivMap.appendChild(sideMapIframe);
};

// filtering (not showing) TRs in table according parameters
function filterTRs() {
  const name = document.getElementById('filterName').value.toLowerCase();
  const city = document.getElementById('filterCity').value;
  const type = document.getElementById('filterType').value;

  data.map(function(e) {
    document.getElementById(e.code).hidden = false;
    if (name && e.name.toLowerCase().indexOf(name) == -1) {
      document.getElementById(e.code).hidden = true;
    };
    if (city && e.city != city) {
      document.getElementById(e.code).hidden = true;
    };
    if (type && e.type != type) {
      document.getElementById(e.code).hidden = true;
    };
  });
};

// mapping event on filter control elements
if (document.getElementById('newFilter')) {
  document.getElementById('filterName').addEventListener('input', filterTRs);
  document.getElementById('filterCity').addEventListener('change', filterTRs);
  document.getElementById('filterType').addEventListener('change', filterTRs);
  document.getElementById('filterSet').addEventListener('click', function() {
    filterTRs();
    tbody.scrollIntoView({behavior: "smooth", block: "start", inline: "nearest"});
  });
  document.getElementById('filterClear').addEventListener('click', function() {
    document.getElementById('filterName').value = '';
    document.getElementById('filterCity').selectedIndex = 0;
    document.getElementById('filterType').selectedIndex = 0;
    filterTRs();
  });
};
